// src/components/Stock.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto'; // Ensure Chart.js is auto-imported

const Stock = ({ ticker }) => {
    const [stockData, setStockData] = useState(null);
    const [chartData, setChartData] = useState({
        labels: [],
        datasets: []
    });

    useEffect(() => {
        axios.get(`http://127.0.0.1:31324/${ticker}`)
            .then(response => {
                if (response.data) {
                    setStockData(response.data);
                    const data = {
                        labels: Array.from({ length: 10 }, (_, i) => `Time ${i}`),
                        datasets: [
                            {
                                label: `Price of ${response.data.name}`,
                                data: Array.from({ length: 10 }, () => response.data.price), // Mock price data
                                fill: false,
                                backgroundColor: 'rgb(75, 192, 192)',
                                borderColor: 'rgba(75, 192, 192, 0.2)',
                            },
                        ],
                    };
                    setChartData(data);
                }
            })
            .catch(error => {
                console.error('Error fetching stock data:', error);
                setStockData(null); // handle errors appropriately
            });
    }, [ticker]);

    // Handle loading state or if data is null
    if (!stockData) return <p>Loading data for {ticker}...</p>;

    return (
        <div className="stock-container">
            <h2>{stockData.name} ({stockData.ticker})</h2>
            <p>Price: ${stockData.price.toFixed(2)}</p>
            <p>Volume: {stockData.volume.toLocaleString()}</p>
            <Line data={chartData} />
        </div>
    );
};

export default Stock;
