// src/components/Stock.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

const Stock = ({ ticker }) => {
    const [stockData, setStockData] = useState({});
    const [chartData, setChartData] = useState({});

    useEffect(() => {
        axios.get(`http://127.0.0.1:31324/${ticker}`)
            .then(response => {
                setStockData(response.data);
                const data = {
                    labels: Array.from({ length: 10 }, (_, i) => i), // Dummy time points
                    datasets: [
                        {
                            label: `Price of ${response.data.name}`,
                            data: Array.from({ length: 10 }, () => response.data.price), // Mock price data
                            fill: false,
                            backgroundColor: 'rgb(75, 192, 192)',
                            borderColor: 'rgba(75, 192, 192, 0.2)',
                        },
                    ],
                };
                setChartData(data);
            })
            .catch(error => {
                console.error('Error fetching stock data:', error);
            });
    }, [ticker]);

    return (
        <div className="stock-container">
            <h2>{stockData.name} ({stockData.ticker})</h2>
            <p>Price: ${stockData.price}</p>
            <p>Volume: {stockData.volume}</p>
            <Line data={chartData} />
        </div>
    );
};

export default Stock;
